import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const API_BASE = "http://localhost:3001/api";

function Tracker() {
  const { id } = useParams();
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`${API_BASE}/tickets/${id}`)
      .then((r) => r.json())
      .then((data) => {
        if (data.ticket) {
          setTicket(data.ticket);
        } else {
          setError("❌ لم يتم العثور على بلاغ بهذا الرقم");
        }
        setLoading(false);
      })
      .catch(() => {
        setError("⚠️ خطأ في الاتصال بالخادم");
        setLoading(false);
      });
  }, [id]);

  const statusColors = {
    Open: "#2196f3",
    "In Progress": "#ff9800",
    Completed: "#4caf50",
    "On Hold": "#f44336",
    Referred: "#9c27b0",
    "Cannot Access": "#795548",
  };

  if (loading) return <p style={{ textAlign: "center" }}>⏳ جاري التحميل...</p>;
  if (error)
    return (
      <div
        style={{
          padding: "12px",
          background: "#ffe6e6",
          border: "1px solid #ff4d4d",
          borderRadius: "8px",
          color: "red",
          maxWidth: "500px",
          margin: "20px auto",
          textAlign: "center",
          direction: "rtl",
        }}
      >
        {error}
      </div>
    );

  const color = statusColors[ticket.status] || "#333";

  return (
    <div
      style={{
        padding: 16,
        maxWidth: 600,
        margin: "auto",
        direction: "rtl",
      }}
    >
      <h2 style={{ color: "#003366", textAlign: "center", marginBottom: "20px" }}>
        📌 متابعة البلاغ
      </h2>
      <div
        style={{
          padding: 20,
          borderRadius: "12px",
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <h3 style={{ marginTop: 0, marginBottom: "10px", color }}>
          الحالة: {ticket.status}
        </h3>
        <p><b>🔢 رقم البلاغ:</b> {ticket.id}</p>
        <p><b>📌 العنوان:</b> {ticket.title}</p>
        <p><b>📝 الوصف:</b> {ticket.description}</p>
        <p><b>⚡ الأولوية:</b> {ticket.priority || "—"}</p>
        <p><b>🏢 الموقع:</b> {ticket.location}</p>
        <p><b>📱 رقم المبلِّغ:</b> {ticket.contact}</p>
        <p><b>💬 ملاحظات عامة:</b> {ticket.public_notes || "لا توجد ملاحظات"}</p>
        {ticket.tech_name && (
          <p><b>👨‍🔧 الفني:</b> {ticket.tech_name} ({ticket.tech_phone || "—"})</p>
        )}
      </div>
    </div>
  );
}

export default Tracker;
