import React, { useState } from "react";

const API_BASE = "http://localhost:3001/api";

function Requester() {
  const [form, setForm] = useState({});
  const [ticketId, setTicketId] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [errorSubmit, setErrorSubmit] = useState(null);
  const [errorTrack, setErrorTrack] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const submitTicket = async (e) => {
    e.preventDefault();
    setErrorSubmit(null);
    setTicketId(null);

    try {
      const res = await fetch(`${API_BASE}/tickets`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (data.ticketId) {
        setTicketId(data.ticketId);
      } else {
        setErrorSubmit("‚ùå ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ®ŸÑÿßÿ∫ÿå ÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™");
      }
    } catch {
      setErrorSubmit("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ");
    }
  };

  const trackByPhone = async () => {
    setErrorTrack(null);
    try {
      const res = await fetch(`${API_BASE}/tickets?contact=${form.contact}`);
      const data = await res.json();
      if (data.tickets && data.tickets.length > 0) {
        setTickets(data.tickets);
      } else {
        setTickets([]);
        setErrorTrack("‚ùå ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸÑÿßÿ∫ÿßÿ™ ŸÖÿ±ÿ™ÿ®ÿ∑ÿ© ÿ®Ÿáÿ∞ÿß ÿßŸÑÿ±ŸÇŸÖ");
      }
    } catch {
      setErrorTrack("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center p-6">
      <div className="w-full max-w-lg bg-white p-6 rounded-xl shadow-lg space-y-4 text-right">
        <h2 className="text-2xl font-bold text-blue-900 mb-4">
          üìã ÿ•ÿ±ÿ≥ÿßŸÑ ÿ®ŸÑÿßÿ∫ ÿ¨ÿØŸäÿØ
        </h2>

        {/* Form */}
        <form onSubmit={submitTicket} className="space-y-3">
          <div>
            <label className="block mb-1 font-medium">üìå ÿπŸÜŸàÿßŸÜ ÿßŸÑÿ®ŸÑÿßÿ∫</label>
            <input
              name="title"
              placeholder="ŸÖÿ´ÿßŸÑ: ÿπÿ∑ŸÑ ŸÅŸä ÿßŸÑŸÖŸÉŸäŸÅ"
              onChange={handleChange}
              required
              className="w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-green-500"
            />
          </div>

          <div>
            <label className="block mb-1 font-medium">üìù ÿßŸÑŸàÿµŸÅ</label>
            <textarea
              name="description"
              placeholder="ÿßŸÉÿ™ÿ® ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÖÿ¥ŸÉŸÑÿ© ŸáŸÜÿß..."
              onChange={handleChange}
              required
              className="w-full border rounded-lg px-3 py-2 min-h-[80px] focus:ring-2 focus:ring-green-500"
            />
          </div>

          <div>
            <label className="block mb-1 font-medium">üè¢ ÿßŸÑÿßÿ≥ÿ™ŸàÿØŸäŸà</label>
            <select
              name="location"
              onChange={handleChange}
              required
              className="w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-green-500"
            >
              <option value="">ÿßÿÆÿ™ÿ± ÿßŸÑÿßÿ≥ÿ™ŸàÿØŸäŸà</option>
              {[...Array(14)].map((_, i) => (
                <option key={i}>Studio {i + 1}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block mb-1 font-medium">üì± ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ</label>
            <input
              name="contact"
              placeholder="ŸÖÿ´ÿßŸÑ: 0555555555"
              onChange={handleChange}
              required
              type="tel"
              className="w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-green-500"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-green-700 text-white py-2 rounded-lg font-bold hover:bg-green-800 transition"
          >
            üì® ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ®ŸÑÿßÿ∫
          </button>
        </form>

        {/* Alerts */}
        {ticketId && (
          <div className="p-3 bg-green-100 border border-green-500 rounded-lg text-green-800 text-sm">
            ‚úÖ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ∞ŸÉÿ±ÿ© ÿ®ŸÜÿ¨ÿßÿ≠! ÿ±ŸÇŸÖŸáÿß:{" "}
            <span className="font-mono font-bold">{ticketId}</span>
          </div>
        )}
        {errorSubmit && (
          <div className="p-3 bg-red-100 border border-red-500 rounded-lg text-red-800 text-sm">
            {errorSubmit}
          </div>
        )}

        {/* Track section */}
        <hr className="my-4" />
        <h3 className="text-lg font-bold text-blue-900">üîé ŸÖÿ™ÿßÿ®ÿπÿ© ÿ®ŸÑÿßÿ∫ÿßÿ™Ÿä</h3>
        <button
          onClick={trackByPhone}
          className="w-full bg-blue-700 text-white py-2 rounded-lg font-bold hover:bg-blue-800 transition mt-2"
        >
          ÿπÿ±ÿ∂ ÿßŸÑÿ®ŸÑÿßÿ∫ÿßÿ™
        </button>

        {errorTrack && (
          <div className="p-3 bg-red-100 border border-red-500 rounded-lg text-red-800 text-sm">
            {errorTrack}
          </div>
        )}
        {tickets.length > 0 && (
          <div className="mt-4 space-y-2">
            {tickets.map((t) => (
              <div
                key={t.id}
                className="p-3 border rounded-lg shadow-sm bg-gray-50 text-sm"
              >
                <div>üìå <b>{t.title}</b></div>
                <div>üìÖ {t.created_at}</div>
                <div>‚ö° {t.priority || "‚Äî"}</div>
                <div>üìç {t.location}</div>
                <div>üì± {t.contact}</div>
                <div>üìä ÿßŸÑÿ≠ÿßŸÑÿ©: {t.status}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Requester;
