import React, { useState } from "react";

const API_BASE = "http://localhost:3001/api";

const translations = {
  ar: {
    newRequest: "إرسال بلاغ جديد",
    title: "عنوان البلاغ",
    desc: "وصف البلاغ",
    contact: "رقم الجوال",
    category: "الفئة (اختياري)",
    priority: "الأولوية (اختياري)",
    location: "الموقع",
    submit: "إرسال",
    myReports: "متابعة بلاغاتي",
    langSwitch: "English",
  },
  en: {
    newRequest: "Submit New Request",
    title: "Request Title",
    desc: "Description",
    contact: "Phone Number",
    category: "Category (optional)",
    priority: "Priority (optional)",
    location: "Location",
    submit: "Submit",
    myReports: "Track My Requests",
    langSwitch: "العربية",
  },
};

function Requester() {
  const [lang, setLang] = useState("ar");
  const t = translations[lang];

  const [form, setForm] = useState({
    title: "",
    description: "",
    contact: "",
    category: "",
    priority: "",
    location: "",
  });
  const [msg, setMsg] = useState("");

  const submitRequest = async () => {
    const res = await fetch(`${API_BASE}/tickets`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    const data = await res.json();
    if (data.ok) {
      setMsg(
        lang === "ar"
          ? `✅ تم إرسال البلاغ. رقم التتبع: ${data.ticketId}`
          : `✅ Request submitted. Tracking ID: ${data.ticketId}`
      );
    }
  };

  return (
    <div style={{ padding: 16, direction: lang === "ar" ? "rtl" : "ltr" }}>
      <button onClick={() => setLang(lang === "ar" ? "en" : "ar")}>
        {t.langSwitch}
      </button>
      <h2>{t.newRequest}</h2>

      <input
        placeholder={t.title}
        value={form.title}
        onChange={(e) => setForm({ ...form, title: e.target.value })}
      />
      <textarea
        placeholder={t.desc}
        value={form.description}
        onChange={(e) => setForm({ ...form, description: e.target.value })}
      />
      <input
        placeholder={t.contact}
        value={form.contact}
        onChange={(e) => setForm({ ...form, contact: e.target.value })}
      />
      <input
        placeholder={t.category}
        value={form.category}
        onChange={(e) => setForm({ ...form, category: e.target.value })}
      />
      <input
        placeholder={t.priority}
        value={form.priority}
        onChange={(e) => setForm({ ...form, priority: e.target.value })}
      />
      <input
        placeholder={t.location}
        value={form.location}
        onChange={(e) => setForm({ ...form, location: e.target.value })}
      />

      <button onClick={submitRequest}>{t.submit}</button>
      {msg && <div>{msg}</div>}

      <h3>{t.myReports}</h3>
    </div>
  );
}

export default Requester;
