import React, { useState } from "react";


const API_BASE = "http://localhost:3001/api";

// üî¢ Normalize Arabic digits to English
const normalizeDigits = (input) => {
  if (!input) return "";
  return input.replace(/[Ÿ†-Ÿ©]/g, (d) => "Ÿ†Ÿ°Ÿ¢Ÿ£Ÿ§Ÿ•Ÿ¶ŸßŸ®Ÿ©".indexOf(d));
};

function Requester() {
  const [form, setForm] = useState({});
  const [customLocation, setCustomLocation] = useState(""); // ‚úÖ new state
  const [ticketId, setTicketId] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [errorSubmit, setErrorSubmit] = useState(null);
  const [errorTrack, setErrorTrack] = useState(null);
  const [trackPhone, setTrackPhone] = useState("");
  const [tab, setTab] = useState("new");

  const handleChange = (e) => {
    let value = e.target.value;
    if (e.target.name === "contact") {
      value = normalizeDigits(value);
    }
    setForm({ ...form, [e.target.name]: value });
  };

  const submitTicket = async (e) => {
    e.preventDefault();
    setErrorSubmit(null);
    setTicketId(null);

    // ‚úÖ ÿ•ÿ∞ÿß ÿßÿÆÿ™ÿßÿ± "ÿ£ÿÆÿ±Ÿâ" ŸÜÿÆÿ≤ŸÜ customLocation
    const payload = {
      ...form,
      location: form.location === "Other" ? customLocation : form.location,
    };

    try {
      const res = await fetch(`${API_BASE}/tickets`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const data = await res.json();
      if (data.ticketId) {
        setTicketId(data.ticketId);
      } else {
        setErrorSubmit("‚ùå ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ®ŸÑÿßÿ∫ÿå ÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™");
      }
    } catch {
      setErrorSubmit("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ");
    }
  };

  const trackByPhone = async () => {
    setErrorTrack(null);
    setTickets([]);
    try {
      const res = await fetch(`${API_BASE}/tickets?contact=${normalizeDigits(trackPhone)}`);
      const data = await res.json();
      if (data.tickets && data.tickets.length > 0) {
        setTickets(data.tickets);
      } else {
        setErrorTrack("‚ùå ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸÑÿßÿ∫ÿßÿ™ ŸÖÿ±ÿ™ÿ®ÿ∑ÿ© ÿ®Ÿáÿ∞ÿß ÿßŸÑÿ±ŸÇŸÖ");
      }
    } catch {
      setErrorTrack("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ");
    }
  };

  return (
