import React, { useEffect, useState } from "react";

const API_BASE = "http://localhost:3001/api";
const ADMIN_PASS = "admin123"; // change later

function OpsDashboard() {
  const [auth, setAuth] = useState(false);
  const [pass, setPass] = useState("");
  const [tickets, setTickets] = useState([]);
  const [kpi, setKpi] = useState({});
  const [error, setError] = useState(null);

  const login = () => {
    if (pass === ADMIN_PASS) setAuth(true);
    else setError("‚ùå ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©");
  };

  const fetchData = () => {
    fetch(`${API_BASE}/tickets-all`)
      .then((r) => r.json())
      .then((data) => setTickets(data.tickets || []))
      .catch(() => setError("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ∞ÿßŸÉÿ±"));

    fetch(`${API_BASE}/tickets-kpi`)
      .then((r) => r.json())
      .then(setKpi)
      .catch(() => setError("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ŸÖÿ§ÿ¥ÿ±ÿßÿ™ ÿßŸÑÿ£ÿØÿßÿ°"));
  };

  useEffect(() => {
    if (auth) fetchData();
  }, [auth]);

  const updateTicket = async (id) => {
    try {
      const res = await fetch(`${API_BASE}/tickets-update`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id,
          status: document.getElementById(`status-${id}`).value,
          public_notes: document.getElementById(`note-${id}`).value,
          tech_name: document.getElementById(`techname-${id}`).value,
          tech_phone: document.getElementById(`techphone-${id}`).value,
        }),
      });
      const data = await res.json();
      if (data.success) fetchData();
      else alert("‚ùå ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´");
    } catch {
      alert("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ");
    }
  };

  if (!auth) {
    return (
      <div style={{ padding: 16, textAlign: "center" }}>
        <h2>üîê Ops Dashboard</h2>
        <input
          type="password"
          placeholder="ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±"
          onChange={(e) => setPass(e.target.value)}
          style={{
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            marginRight: "8px",
          }}
        />
        <button
          onClick={login}
          style={{
            background: "#006633",
            color: "#fff",
            padding: "10px 16px",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          ÿØÿÆŸàŸÑ
        </button>
        {error && (
          <p style={{ color: "red", marginTop: "10px" }}>{error}</p>
        )}
      </div>
    );
  }

  return (
    <div style={{ padding: 16, direction: "rtl" }}>
      <h2 style={{ color: "#003366" }}>ŸÑŸàÿ≠ÿ© ÿßŸÑÿµŸäÿßŸÜÿ©</h2>

      {/* KPI Cards */}
      <div style={{ display: "flex", gap: 16, marginBottom: 20, flexWrap: "wrap" }}>
        <div style={kpiCard}>üìå ŸÖŸÅÿ™Ÿàÿ≠: {kpi.open}</div>
        <div style={kpiCard}>üöß ŸÇŸäÿØ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞: {kpi.in_progress}</div>
        <div style={kpiCard}>‚úÖ ŸÖŸÉÿ™ŸÖŸÑ: {kpi.completed}</div>
        <div style={kpiCard}>‚è∏Ô∏è ŸÖÿπŸÑŸëŸÇ: {kpi.on_hold}</div>
        <div style={kpiCard}>üîÑ ŸÖÿ≠ŸàŸëŸÑ: {kpi.referred}</div>
        <div style={kpiCard}>üö´ ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑŸàÿµŸàŸÑ: {kpi.cannot_access}</div>
      </div>

      <a
        href={`${API_BASE}/tickets-export`}
        target="_blank"
        rel="noreferrer"
        style={{
          display: "inline-block",
          padding: "10px 16px",
          background: "#006633",
          color: "#fff",
          borderRadius: "6px",
          marginBottom: "16px",
          textDecoration: "none",
        }}
      >
        ‚¨áÔ∏è ÿ™ÿ≠ŸÖŸäŸÑ CSV
      </a>

      {/* Tickets Table */}
      {tickets.length === 0 ? (
        <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ∞ÿßŸÉÿ±</p>
      ) : (
        <table
          border="1"
          cellPadding="6"
          style={{ borderCollapse: "collapse", width: "100%", background: "#fff" }}
        >
          <thead style={{ background: "#003366", color: "#fff" }}>
            <tr>
