import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const API_BASE = "http://localhost:3001/api";

const translations = {
  ar: {
    title: "تتبع البلاغ",
    status: "الحالة",
    category: "الفئة",
    priority: "الأولوية",
    location: "الموقع",
    notes: "الملاحظات العامة",
    langSwitch: "English",
  },
  en: {
    title: "Track Request",
    status: "Status",
    category: "Category",
    priority: "Priority",
    location: "Location",
    notes: "Public Notes",
    langSwitch: "العربية",
  },
};

function Tracker() {
  const { id } = useParams();
  const [lang, setLang] = useState("ar");
  const t = translations[lang];

  const [ticket, setTicket] = useState(null);

  useEffect(() => {
    fetch(`${API_BASE}/tickets/${id}`)
      .then((r) => r.json())
      .then((d) => setTicket(d.ticket));
  }, [id]);

  if (!ticket) return <div>Loading...</div>;

  return (
    <div style={{ padding: 16, direction: lang === "ar" ? "rtl" : "ltr" }}>
      <button onClick={() => setLang(lang === "ar" ? "en" : "ar")}>
        {t.langSwitch}
      </button>

      <h2>{t.title}</h2>
      <div>{t.status}: {ticket.status}</div>
      <div>{t.category}: {ticket.category}</div>
      <div>{t.priority}: {ticket.priority}</div>
      <div>{t.location}: {ticket.location}</div>
      <div>{t.notes}: {ticket.public_notes}</div>
    </div>
  );
}

export default Tracker;
