import React, { useState } from "react";

const API_BASE = "http://localhost:3001/api";

function Requester() {
  const [form, setForm] = useState({
    title: "",
    description: "",
    category: "",
    priority: "",
    location: "",
    contact: ""
  });
  const [ticketId, setTicketId] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const submitTicket = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const res = await fetch(`${API_BASE}/tickets`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (data.ticketId) {
        setTicketId(data.ticketId);
      } else {
        setError("❌ لم يتم إنشاء البلاغ، تحقق من البيانات");
      }
    } catch (err) {
      setError("⚠️ خطأ في الاتصال بالخادم");
    }
  };

  const trackByPhone = async () => {
    if (!form.contact) {
      setError("⚠️ أدخل رقم الجوال أولاً");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${API_BASE}/tickets?contact=${form.contact}`);
      const data = await res.json();
      if (data.tickets && data.tickets.length > 0) {
        setTickets(data.tickets);
      } else {
        setTickets([]);
        setError("❌ لا توجد بلاغات مرتبطة بهذا الرقم");
      }
    } catch (err) {
      setError("⚠️ خطأ في الاتصال بالخادم");
    }
    setLoading(false);
  };

  const inputStyle = {
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "14px",
    width: "100%",
  };

  const buttonStyle = {
    background: "#006633",
    color: "#fff",
    padding: "12px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "16px",
  };

  return (
    <div style={{ padding: 16, maxWidth: 500, margin: "auto", direction: "rtl" }}>
      <h2 style={{ color: "#003366", textAlign: "center", marginBottom: 20 }}>
        📋 إرسال بلاغ جديد
      </h2>

 <form
  onSubmit={submitTicket}
  style={{
    display: "flex",
    flexDirection: "column",
    gap: 12,
    background: "#fff",
    padding: 20,
    borderRadius: "12px",
    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
  }}
>
  <label>
    📌 عنوان البلاغ
    <input 
      name="title" 
      placeholder="مثال: عطل في المكيف" 
      onChange={handleChange} 
      style={inputStyle} 
      required 
    />
  </label>

  <label>
    📝 الوصف
    <textarea
      name="description"
      placeholder="اكتب تفاصيل المشكلة هنا..."
      onChange={handleChange}
      style={{ ...inputStyle, minHeight: "80px" }}
      required
    />
  </label>

  <label>
    🏷️ الفئة
    <select name="category" onChange={handleChange} style={inputStyle} required>
      <option value="">اختر الفئة</option>
     <option>Equipment / معدات</option>
      <option>Electrical / كهرباء</option>
      <option>AC / تكييف</option>
      <option>Cleaning / نظافة</option>
    </select>
  </label>

  <label>
    ⚡ الأولوية
    <select name="priority" onChange={handleChange} style={inputStyle} required>
      <option value="">اختر مستوى الأولوية</option>
      <option>High</option>
      <option>Medium</option>
      <option>Low</option>
    </select>
  </label>

  <label>
    🏢 الاستوديو
    <select name="location" onChange={handleChange} style={inputStyle} required>
      <option value="">اختر الاستوديو</option>
      {[...Array(14)].map((_, i) => (
        <option key={i}>Studio {i + 1}</option>
      ))}
    </select>
  </label>

  <label>
    📱 رقم الجوال

    <input 
      name="contact" 
      placeholder="مثال: 0555555555" 
      onChange={handleChange} 
      style={inputStyle} 
      required
      type="tel"
    />
  </label>


  <button type="submit" style={buttonStyle}>📨 إرسال البلاغ</button>
</form>

      {ticketId && (
        <div
          style={{
            marginTop: 20,
            padding: 16,
            background: "#e6ffed",
            border: "1px solid #00a152",
            borderRadius: "8px",
            color: "#006633",
          }}
        >
          ✅ تم إنشاء التذكرة بنجاح <br /> رقمها: <b>{ticketId}</b>
        </div>
      )}

      {error && (
        <div
          style={{
            marginTop: 16,
            padding: 12,
            background: "#ffe6e6",
            border: "1px solid #ff4d4d",
            borderRadius: "8px",
            color: "red",
          }}
        >
          {error}
        </div>
      )}

      <hr style={{ margin: "24px 0" }} />

      <h3 style={{ color: "#003366" }}>🔎 متابعة بلاغاتي</h3>
      <button onClick={trackByPhone} style={buttonStyle}>
        📂 عرض البلاغات
      </button>

      {loading && <p>⏳ جاري تحميل البلاغات...</p>}

      <ul style={{ listStyle: "none", padding: 0, marginTop: 12 }}>
        {tickets.map((t) => (
          <li
            key={t.id}
            style={{
              background: "#f0f8ff",
              marginBottom: 8,
              padding: 10,
              borderRadius: "8px",
              border: "1px solid #ccc",
            }}
          >
            <b>{t.id}</b> - {t.status} - {t.location}
          </li>
        ))}
      </ul>
    </div>
  );
}

const validatePhone = (phone) => {
  const regex = /^05\d{8}$/; // يبدأ بـ05 + 8 أرقام
  return regex.test(phone);
};

const submitTicket = async (e) => {
  e.preventDefault();
  setErrorSubmit(null);

  if (!validatePhone(form.contact)) {
    setErrorSubmit("❌ رقم الجوال يجب أن يبدأ بـ 05 ويتكون من 10 أرقام");
    return;
  }

  try {
    const res = await fetch(`${API_BASE}/tickets`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    const data = await res.json();
    if (data.ticketId) {
      setTicketId(data.ticketId);
    } else {
      setErrorSubmit("❌ لم يتم إنشاء البلاغ، تحقق من البيانات");
    }
  } catch {
    setErrorSubmit("⚠️ خطأ في الاتصال بالخادم");
  }
};

export default Requester;
