import React, { useEffect, useState } from "react";

const API_BASE = "http://localhost:3001/api";
const ADMIN_PASS = "admin123"; // change later

function OpsDashboard() {
  const [auth, setAuth] = useState(false);
  const [pass, setPass] = useState("");
  const [tickets, setTickets] = useState([]);
  const [kpi, setKpi] = useState({});

  useEffect(() => {
    if (auth) {
      fetch(`${API_BASE}/tickets?contact=`) // get all (you can extend backend later)
      fetch(`${API_BASE}/tickets-kpi`).then(r=>r.json()).then(setKpi);
    }
  }, [auth]);

  const login = () => {
    if (pass === ADMIN_PASS) setAuth(true);
  };

  if (!auth) {
    return (
      <div style={{padding:16}}>
        <h2>Ops Dashboard</h2>
        <input type="password" placeholder="Password" onChange={e=>setPass(e.target.value)} />
        <button onClick={login}>Login</button>
      </div>
    );
  }

  return (
    <div style={{padding:16}}>
      <h2>Dashboard</h2>
      <div style={{display:"flex", gap:16}}>
        <div>Open: {kpi.open}</div>
        <div>In Progress: {kpi.in_progress}</div>
        <div>Completed: {kpi.completed}</div>
        <div>On Hold: {kpi.on_hold}</div>
      </div>
      <a href={`${API_BASE}/tickets-export`} target="_blank" rel="noreferrer">
        ⬇️ Download CSV
      </a>
    </div>
  );
}

export default OpsDashboard;
