import React, { useEffect, useState } from "react";

const API_BASE = "http://localhost:3001/api";
const ADMIN_PASS = "admin123"; // change later

function OpsDashboard() {
  const [auth, setAuth] = useState(false);
  const [pass, setPass] = useState("");
  const [tickets, setTickets] = useState([]);
  const [kpi, setKpi] = useState({});
  const [sortBy, setSortBy] = useState("created_at");
  const [filterStatus, setFilterStatus] = useState("");
  const [filterCategory, setFilterCategory] = useState("");
  const [view, setView] = useState("table"); // table | cards
  const [page, setPage] = useState(1);
  const pageSize = 8;

  // ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
  const fetchData = async () => {
    try {
      const res = await fetch(`${API_BASE}/tickets`);
      const data = await res.json();
      setTickets(data.tickets || []);

      const kpiRes = await fetch(`${API_BASE}/tickets-kpi`);
      const kpiData = await kpiRes.json();
      setKpi(kpiData);
    } catch (err) {
      console.error("‚ùå Fetch error", err);
    }
  };

  useEffect(() => {
    if (auth) {
      fetchData();
    }
  }, [auth]);

  // ÿ™ÿ≠ÿØŸäÿ´ ÿ™ÿ∞ŸÉÿ±ÿ©
  const updateTicket = async (id, field, value) => {
    try {
      await fetch(`${API_BASE}/tickets/${id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ [field]: value }),
      });
      // ÿ™ÿ≠ÿØŸäÿ´ ŸÖÿ≠ŸÑŸä
      setTickets((prev) =>
        prev.map((t) => (t.id === id ? { ...t, [field]: value } : t))
      );
    } catch {
      alert("‚ùå ŸÅÿ¥ŸÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™");
    }
  };

  // ŸÅŸÑÿ™ÿ±ÿ© Ÿàÿ™ÿ±ÿ™Ÿäÿ®
  let displayedTickets = [...tickets];

  if (filterStatus) {
    displayedTickets = displayedTickets.filter(
      (t) => t.status === filterStatus
    );
  }
  if (filterCategory) {
    displayedTickets = displayedTickets.filter(
      (t) => t.category === filterCategory
    );
  }

  if (sortBy === "created_at") {
    displayedTickets.sort(
      (a, b) => new Date(b.created_at) - new Date(a.created_at)
    );
  } else if (sortBy === "priority") {
    const order = { High: 1, Medium: 2, Low: 3 };
    displayedTickets.sort(
      (a, b) => (order[a.priority] || 99) - (order[b.priority] || 99)
    );
  } else if (sortBy === "status") {
    displayedTickets.sort((a, b) => a.status.localeCompare(b.status));
  }

  // Pagination
  const totalPages = Math.ceil(displayedTickets.length / pageSize);
  const visibleTickets = displayedTickets.slice(
    (page - 1) * pageSize,
    page * pageSize
  );

  const login = () => {
    if (pass === ADMIN_PASS) setAuth(true);
  };

  if (!auth) {
    return (
      <div style={{ padding: 16 }}>
        <h2>üîí Ops Dashboard</h2>
        <input
          type="password"
          placeholder="Password"
          onChange={(e) => setPass(e.target.value)}
        />
        <button onClick={login}>Login</button>
      </div>
    );
  }

  return (
    <div style={{ padding: 16, direction: "rtl", fontFamily: "Tahoma" }}>
      <h2>ŸÑŸàÿ≠ÿ© ÿßŸÑÿµŸäÿßŸÜÿ© üõ†Ô∏è</h2>

      {/* KPI Cards */}
      <div style={{ display: "flex", gap: 12, marginBottom: 20 }}>
        <div>üîß ŸÖŸÅÿ™Ÿàÿ≠: {kpi.open || 0}</div>
        <div>üìù ŸÇŸäÿØ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞: {kpi.in_progress || 0}</div>
        <div>‚úÖ ŸÖŸÉÿ™ŸÖŸÑ: {kpi.completed || 0}</div>
        <div>‚è∏Ô∏è ŸÖÿπŸÑŸÇ: {kpi.on_hold || 0}</div>
        <div>üîÑ ŸÖÿ≠ÿßŸÑ: {kpi.referred || 0}</div>
        <div>‚ùå ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑŸàÿµŸàŸÑ: {kpi.cannot_access || 0}</div>
      </div>

      {/* Controls */}
      <div style={{ display: "flex", gap: 12, marginBottom: 16 }}>
        <select onChange={(e) => setSortBy(e.target.value)}>
          <option value="created_at">üìÖ ÿßŸÑÿ™ÿßÿ±ŸäÿÆ</option>
          <option value="priority">‚ö° ÿßŸÑÿ£ŸàŸÑŸàŸäÿ©</option>
          <option value="status">üìå ÿßŸÑÿ≠ÿßŸÑÿ©</option>
        </select>

        <select onChange={(e) => setFilterStatus(e.target.value)}>
          <option value="">ŸÉŸÑ ÿßŸÑÿ≠ÿßŸÑÿßÿ™</option>
          <option value="Open">ŸÖŸÅÿ™Ÿàÿ≠</option>
          <option value="In Progress">ŸÇŸäÿØ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞</option>
          <option value="Completed">ŸÖŸÉÿ™ŸÖŸÑ</option>
          <option value="On Hold">ŸÖÿπŸÑŸÇ</option>
          <option value="Referred">ŸÖÿ≠ÿßŸÑ</option>
          <option value="Cannot Access">ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑŸàÿµŸàŸÑ</option>
        </select>

        <select onChange={(e) => setFilterCategory(e.target.value)}>
          <option value="">ŸÉŸÑ ÿßŸÑŸÅÿ¶ÿßÿ™</option>
          <option value="Electrical">ŸÉŸáÿ±ÿ®ÿßÿ°</option>
          <option value="Mechanical">ŸÖŸäŸÉÿßŸÜŸäŸÉÿß</option>
          <option value="Plumbing">ÿ≥ÿ®ÿßŸÉÿ©</option>
          <option value="General">ÿ£ÿÆÿ±Ÿâ</option>
        </select>

        <button onClick={() => setView("table")}>üìã ÿ¨ÿØŸàŸÑ</button>
        <button onClick={() => setView("cards")}>üóÇÔ∏è ÿ®ÿ∑ÿßŸÇÿßÿ™</button>

        <a
          href={`${API_BASE}/tickets-export`}
          target="_blank"
          rel="noreferrer"
          style={{ marginRight: "auto" }}
        >
          ‚¨áÔ∏è ÿ™ÿ≠ŸÖŸäŸÑ CSV
        </a>
      </div>

      {/* ÿ¨ÿØŸàŸÑ */}
      {view === "table" && (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ background: "#003366", color: "#fff" }}>
              <th>ÿßŸÑÿ±ŸÇŸÖ</th>
              <th>ÿßŸÑÿπŸÜŸàÿßŸÜ</th>
              <th>ÿßŸÑŸàÿµŸÅ</th>
              <th>ÿßŸÑŸÅÿ¶ÿ©</th>
              <th>ÿßŸÑÿ£ŸàŸÑŸàŸäÿ©</th>
              <th>ÿßŸÑŸÖŸàŸÇÿπ</th>
              <th>ÿßŸÑÿ™ÿßÿ±ŸäÿÆ</th>
              <th>ÿßŸÑÿ≠ÿßŸÑÿ©</th>
            </tr>
          </thead>
          <tbody>
            {visibleTickets.map((t) => (
              <tr key={t.id} style={{ borderBottom: "1px solid #ddd" }}>
                <td>{t.id}</td>
                <td>{t.title}</td>
                <td>
                  <a
                    href={`/track/${t.id}`}
                    target="_blank"
                    rel="noreferrer"
                    style={{ color: "#003366" }}
                  >
                    {t.description && t.description.length > 40
                      ? t.description.substring(0, 40) + "..."
                      : t.description}
                  </a>
                </td>
                <td>
                  <select
                    value={t.category || ""}
                    onChange={(e) =>
                      updateTicket(t.id, "category", e.target.value)
                    }
                  >
                    <option value="">‚Äî</option>
                    <option value="Electrical">ŸÉŸáÿ±ÿ®ÿßÿ°</option>
                    <option value="Mechanical">ŸÖŸäŸÉÿßŸÜŸäŸÉÿß</option>
                    <option value="Plumbing">ÿ≥ÿ®ÿßŸÉÿ©</option>
                    <option value="General">ÿ£ÿÆÿ±Ÿâ</option>
                  </select>
                </td>
                <td>
                  <select
                    value={t.priority || ""}
                    onChange={(e) =>
                      updateTicket(t.id, "priority", e.target.value)
                    }
                  >
                    <option value="High">ÿπÿßŸÑŸäÿ©</option>
                    <option value="Medium">ŸÖÿ™Ÿàÿ≥ÿ∑ÿ©</option>
                    <option value="Low">ŸÖŸÜÿÆŸÅÿ∂ÿ©</option>
                  </select>
                </td>
                <td>{t.location}</td>
                <td>
                  {t.created_at
                    ? new Date(t.created_at).toLocaleString("ar-SA")
                    : "‚Äî"}
                </td>
                <td>{t.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* ÿ®ÿ∑ÿßŸÇÿßÿ™ */}
      {view === "cards" && (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
            gap: 16,
          }}
        >
          {visibleTickets.map((t) => {
            const priorityColors = {
              High: "#ffcccc",
              Medium: "#fff2cc",
              Low: "#d9f2d9",
            };
            return (
              <div
                key={t.id}
                style={{
                  background: priorityColors[t.priority] || "#eee",
                  padding: 16,
                  borderRadius: 8,
                  boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
                }}
              >
                <h4>{t.title} ({t.id})</h4>
                <p>
                  {t.description && t.description.length > 60
                    ? t.description.substring(0, 60) + "..."
                    : t.description}
                </p>
                <p>
                  üóÇÔ∏è{" "}
                  <select
                    value={t.category || ""}
                    onChange={(e) =>
                      updateTicket(t.id, "category", e.target.value)
                    }
                  >
                    <option value="">‚Äî</option>
                    <option value="Electrical">ŸÉŸáÿ±ÿ®ÿßÿ°</option>
                    <option value="Mechanical">ŸÖŸäŸÉÿßŸÜŸäŸÉÿß</option>
                    <option value="Plumbing">ÿ≥ÿ®ÿßŸÉÿ©</option>
                    <option value="General">ÿ£ÿÆÿ±Ÿâ</option>
                  </select>
                </p>
                <p>
                  ‚ö°{" "}
                  <select
                    value={t.priority || ""}
                    onChange={(e) =>
                      updateTicket(t.id, "priority", e.target.value)
                    }
                  >
                    <option value="High">ÿπÿßŸÑŸäÿ©</option>
                    <option value="Medium">ŸÖÿ™Ÿàÿ≥ÿ∑ÿ©</option>
                    <option value="Low">ŸÖŸÜÿÆŸÅÿ∂ÿ©</option>
                  </select>
                </p>
                <p>üè¢ {t.location}</p>
                <p>
                  üìÖ{" "}
                  {t.created_at
                    ? new Date(t.created_at).toLocaleString("ar-SA")
                    : "‚Äî"}
                </p>
                <p>üìå {t.status}</p>
                <a
                  href={`/track/${t.id}`}
                  target="_blank"
                  rel="noreferrer"
                  style={{
                    display: "inline-block",
                    marginTop: 8,
                    padding: "6px 12px",
                    background: "#003366",
                    color: "#fff",
                    borderRadius: 6,
                    textDecoration: "none",
                  }}
                >
                  üîé ÿπÿ±ÿ∂ ŸÉÿßŸÖŸÑ
                </a>
              </div>
            );
          })}
        </div>
      )}

      {/* Pagination */}
      <div
        style={{
          marginTop: 20,
          display: "flex",
          justifyContent: "center",
          gap: 8,
        }}
      >
        <button onClick={() => setPage((p) => Math.max(p - 1, 1))} disabled={page === 1}>
          ‚óÄ ÿßŸÑÿ≥ÿßÿ®ŸÇ
        </button>
        <span>
          ÿßŸÑÿµŸÅÿ≠ÿ© {page} ŸÖŸÜ {totalPages}
        </span>
        <button
          onClick={() => setPage((p) => Math.min(p + 1, totalPages))}
          disabled={page === totalPages}
        >
          ÿßŸÑÿ™ÿßŸÑŸä ‚ñ∂
        </button>
      </div>
    </div>
  );
}

export default OpsDashboard;
