import React, { useEffect, useState } from "react";

const API_BASE = "http://localhost:3001/api";
const ADMIN_PASS = "admin123"; // change later

function OpsDashboard() {
  const [auth, setAuth] = useState(false);
  const [pass, setPass] = useState("");
  const [tickets, setTickets] = useState([]);
  const [kpi, setKpi] = useState({});
  const [error, setError] = useState(null);

  const login = () => {
    if (pass === ADMIN_PASS) setAuth(true);
    else setError("❌ كلمة المرور غير صحيحة");
  };

  const fetchData = () => {
    fetch(`${API_BASE}/tickets-all`)
      .then((r) => r.json())
      .then((data) => setTickets(data.tickets || []))
      .catch(() => setError("⚠️ خطأ في جلب التذاكر"));

    fetch(`${API_BASE}/tickets-kpi`)
      .then((r) => r.json())
      .then(setKpi)
      .catch(() => setError("⚠️ خطأ في جلب مؤشرات الأداء"));
  };

  useEffect(() => {
    if (auth) fetchData();
  }, [auth]);

  const updateTicket = async (id, newStatus, newNote) => {
    try {
      const res = await fetch(`${API_BASE}/tickets-update`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ id, status: newStatus, public_notes: newNote }),
      });
      const data = await res.json();
      if (data.success) {
        fetchData();
      } else {
        alert("❌ لم يتم التحديث");
      }
    } catch (err) {
      alert("⚠️ خطأ في الاتصال بالخادم");
    }
  };

  if (!auth) {
    return (
      <div style={{ padding: 16 }}>
        <h2>Ops Dashboard</h2>
        <input
          type="password"
          placeholder="Password"
          onChange={(e) => setPass(e.target.value)}
        />
        <button onClick={login}>Login</button>
        {error && <p style={{ color: "red" }}>{error}</p>}
      </div>
    );
  }

  return (
    <div style={{ padding: 16, direction: "rtl" }}>
      <h2>لوحة الصيانة</h2>

      {/* KPI cards */}
      <div style={{ display: "flex", gap: 16, marginBottom: 16 }}>
        <div>📌 Open: {kpi.open}</div>
        <div>🚧 In Progress: {kpi.in_progress}</div>
        <div>✅ Completed: {kpi.completed}</div>
        <div>⏸️ On Hold: {kpi.on_hold}</div>
        <div>🔄 Referred: {kpi.referred}</div>
        <div>🚫 Cannot Access: {kpi.cannot_access}</div>
      </div>

      {/* CSV export */}
      <a
        href={`${API_BASE}/tickets-export`}
        target="_blank"
        rel="noreferrer"
        style={{ display: "block", marginBottom: 16 }}
      >
        ⬇️ تحميل CSV
      </a>

      {/* Tickets table */}
      <h3>جميع التذاكر</h3>
      {tickets.length === 0 ? (
        <p>لا توجد تذاكر مسجلة</p>
      ) : (
        <table
          border="1"
          cellPadding="6"
          style={{ borderCollapse: "collapse", width: "100%" }}
        >
          <thead>
            <tr>
              <th>الرقم</th>
              <th>العنوان</th>
              <th>الوصف</th>
              <th>الفئة</th>
              <th>الأولوية</th>
              <th>الموقع</th>
              <th>الجوال</th>
              <th>الحالة</th>
              <th>ملاحظات عامة</th>
              <th>إجراءات</th>
            </tr>
          </thead>
          <tbody>
            {tickets.map((t) => (
              <tr key={t.id}>
                <td>{t.id}</td>
                <td>{t.title}</td>
                <td>{t.description}</td>
                <td>{t.category}</td>
                <td>{t.priority}</td>
                <td>{t.location}</td>
                <td>{t.contact}</td>
                <td>
                  <select defaultValue={t.status} id={`status-${t.id}`}>
                    <option>Open</option>
                    <option>In Progress</option>
                    <option>Completed</option>
                    <option>On Hold</option>
                    <option>Referred</option>
                    <option>Cannot Access</option>
                  </select>
                </td>
                <td>
                  <input
                    type="text"
                    defaultValue={t.public_notes}
                    id={`note-${t.id}`}
                  />
                </td>
                <td>
                  <button
                    onClick={() =>
                      updateTicket(
                        t.id,
                        document.getElementById(`status-${t.id}`).value,
                        document.getElementById(`note-${t.id}`).value
                      )
                    }
                  >
                    💾 حفظ
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default OpsDashboard;
