import React, { useState } from "react";

const API_BASE = "http://localhost:3001/api";

// 🔢 Normalize Arabic digits to English
const normalizeDigits = (input) => {
  if (!input) return "";
  return input.replace(/[٠-٩]/g, (d) => "٠١٢٣٤٥٦٧٨٩".indexOf(d));
};

function Requester() {
  const [form, setForm] = useState({});
  const [ticketId, setTicketId] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [errorSubmit, setErrorSubmit] = useState(null);
  const [errorTrack, setErrorTrack] = useState(null);

  const handleChange = (e) => {
    let value = e.target.value;
    if (e.target.name === "contact") {
      value = normalizeDigits(value); // ✅ support Arabic numbers
    }
    setForm({ ...form, [e.target.name]: value });
  };

  const submitTicket = async (e) => {
    e.preventDefault();
    setErrorSubmit(null);
    setTicketId(null);

    try {
      const res = await fetch(`${API_BASE}/tickets`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (data.ticketId) {
        setTicketId(data.ticketId);
      } else {
        setErrorSubmit("❌ لم يتم إنشاء البلاغ، تحقق من البيانات");
      }
    } catch {
      setErrorSubmit("⚠️ خطأ في الاتصال بالخادم");
    }
  };

  const trackByPhone = async () => {
    setErrorTrack(null);
    try {
      const res = await fetch(`${API_BASE}/tickets?contact=${form.contact}`);
      const data = await res.json();
      if (data.tickets && data.tickets.length > 0) {
        setTickets(data.tickets);
      } else {
        setTickets([]);
        setErrorTrack("❌ لا توجد بلاغات مرتبطة بهذا الرقم");
      }
    } catch {
      setErrorTrack("⚠️ خطأ في الاتصال بالخادم");
    }
  };

  return (
    <div style={{ minHeight: "100vh", background: "#f8f9fa", padding: 20, direction: "rtl" }}>
      <div style={{ maxWidth: 600, margin: "auto", background: "#fff", padding: 20, borderRadius: 12, boxShadow: "0 2px 6px rgba(0,0,0,0.1)" }}>
        <h2 style={{ color: "#003366", textAlign: "center", marginBottom: 20 }}>
          📋 إرسال بلاغ جديد
        </h2>

        {/* Form */}
        <form onSubmit={submitTicket} style={{ display: "flex", flexDirection: "column", gap: 12 }}>
          <label>
            عنوان البلاغ 📌
            <input
              name="title"
              placeholder="مثال: عطل في المكيف"
              onChange={handleChange}
              required
              style={{ width: "100%", padding: 10, borderRadius: 6, border: "1px solid #ccc" }}
            />
          </label>

          <label>
            الوصف 📝
            <textarea
              name="description"
              placeholder="اكتب تفاصيل المشكلة هنا..."
              onChange={handleChange}
              required
              style={{ width: "100%", padding: 10, borderRadius: 6, border: "1px solid #ccc", minHeight: 80 }}
            />
          </label>

          <label>
            الاستوديو 🏢
            <select
              name="location"
              onChange={handleChange}
              required
              style={{ width: "100%", padding: 10, borderRadius: 6, border: "1px solid #ccc" }}
            >
              <option value="">اختر الاستوديو</option>
              {[...Array(14)].map((_, i) => (
                <option key={i}>Studio {i + 1}</option>
              ))}
            </select>
          </label>

          <label>
            رقم الجوال 📱
            <input
              name="contact"
              placeholder="مثال: 0555555555"
              onChange={handleChange}
              required
              type="tel"
              style={{ width: "100%", padding: 10, borderRadius: 6, border: "1px solid #ccc" }}
            />
          </label>

          <button
            type="submit"
            style={{
              background: "#006633",
              color: "#fff",
              padding: "12px",
              border: "none",
              borderRadius: "6px",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            📨 إرسال البلاغ
          </button>
        </form>

        {/* Alerts */}
        {ticketId && (
          <div style={{ marginTop: 12, padding: 12, background: "#e6ffe6", border: "1px solid #00b300", borderRadius: 8, color: "#006600" }}>
            ✅ تم إنشاء التذكرة بنجاح! رقمها:{" "}
            <span style={{ fontFamily: "monospace", fontWeight: "bold" }}>{ticketId}</span>
          </div>
        )}
        {errorSubmit && (
          <div style={{ marginTop: 12, padding: 12, background: "#ffe6e6", border: "1px solid #ff4d4d", borderRadius: 8, color: "red" }}>
            {errorSubmit}
          </div>
        )}

        {/* Track section */}
        <hr style={{ margin: "20px 0" }} />
        <h3 style={{ color: "#003366" }}>🔎 متابعة بلاغاتي</h3>
        <button
          onClick={trackByPhone}
          style={{
            background: "#006633",
            color: "#fff",
            padding: "10px",
            border: "none",
            borderRadius: "6px",
            fontWeight: "bold",
            cursor: "pointer",
            marginTop: 8,
          }}
        >
          📂 عرض البلاغات
        </button>

        {errorTrack && (
          <div style={{ marginTop: 12, padding: 12, background: "#ffe6e6", border: "1px solid #ff4d4d", borderRadius: 8, color: "red" }}>
            {errorTrack}
          </div>
        )}

        {tickets.length > 0 && (
          <div style={{ marginTop: 16 }}>
            {tickets.map((t) => (
              <a
                key={t.id}
                href={`/track/${t.id}`} // ✅ opens tracking page
                style={{
                  display: "block",
                  padding: "12px",
                  marginBottom: "8px",
                  borderRadius: "8px",
                  background: "#f1f1f1",
                  border: "1px solid #ddd",
                  textDecoration: "none",
                  color: "#003366",
                  fontWeight: "bold",
                }}
              >
                {t.id} - {t.status} - {t.location}
              </a>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Requester;
