import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const API_BASE = "http://localhost:3001/api";

function Tracker() {
  const { id } = useParams();
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`${API_BASE}/tickets/${id}`)
      .then((r) => r.json())
      .then((data) => {
        if (data.ticket) {
          setTicket(data.ticket);
        } else {
          setError("❌ لم يتم العثور على بلاغ بهذا الرقم");
        }
        setLoading(false);
      })
      .catch(() => {
        setError("⚠️ خطأ في الاتصال بالخادم");
        setLoading(false);
      });
  }, [id]);

  const statusColors = {
    open: "#2196f3",
    "in progress": "#ff9800",
    completed: "#4caf50",
    "on hold": "#f44336",
    referred: "#9c27b0",
    "cannot access": "#795548",
  };

  if (loading) return <p>⏳ جاري التحميل...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  const color = statusColors[ticket.status?.toLowerCase()] || "#333";

  return (
    <div style={{ padding: 16, maxWidth: 500, margin: "auto", direction: "rtl" }}>
      <h2 style={{ color: "#003366", textAlign: "center" }}>📌 متابعة البلاغ</h2>
      <div
        style={{
          padding: 20,
          borderRadius: "12px",
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
          marginTop: 16,
        }}
      >
        <h3 style={{ margin: 0, color }}>{ticket.status}</h3>
        <p><b>رقم البلاغ:</b> {ticket.id}</p>
        <p><b>العنوان:</b> {ticket.title}</p>
        <p><b>الوصف:</b> {ticket.description}</p>
        <p><b>الأولوية:</b> {ticket.priority}</p>
        <p><b>الموقع:</b> {ticket.location}</p>
        <p><b>ملاحظات عامة:</b> {ticket.public_notes || "لا توجد ملاحظات"}</p>
      </div>
    </div>
  );
}

export default Tracker;
