import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const API_BASE = "http://localhost:3001/api";

function Tracker() {
  const { id } = useParams();
  const [ticket, setTicket] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTicket = async () => {
      try {
        const res = await fetch(`${API_BASE}/tickets/${id}`);
        const data = await res.json();
        if (data.ticket) {
          setTicket(data.ticket);
        } else {
          setError("❌ لم يتم العثور على البلاغ");
        }
      } catch {
        setError("⚠️ خطأ في الاتصال بالخادم");
      }
    };
    fetchTicket();
  }, [id]);

  if (error) {
    return (
      <div style={{ padding: 20, direction: "rtl", color: "red" }}>{error}</div>
    );
  }

  if (!ticket) {
    return (
      <div style={{ padding: 20, direction: "rtl" }}>⏳ جاري التحميل...</div>
    );
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#f8f9fa",
        padding: 20,
        direction: "rtl",
      }}
    >
      <div
        style={{
          maxWidth: 600,
          margin: "auto",
          background: "#fff",
          padding: 20,
          borderRadius: 12,
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <h2 style={{ marginBottom: 20, color: "#003366" }}>
          متابعة البلاغ 📌
        </h2>

        <div style={{ marginBottom: 8, color: "#660066", fontWeight: "bold" }}>
          الحالة: {ticket.status}
        </div>

        <div style={{ marginBottom: 8 }}>🔢 <strong>رقم البلاغ:</strong> {ticket.id}</div>
        <div style={{ marginBottom: 8 }}>📌 <strong>العنوان:</strong> {ticket.title}</div>
        <div style={{ marginBottom: 8 }}>📝 <strong>الوصف:</strong> {ticket.description}</div>

        {/* ✅ الفئة */}
        <div style={{ marginBottom: 8 }}>
          🗂️ <strong>الفئة:</strong> {ticket.category || "—"}
        </div>

        <div style={{ marginBottom: 8 }}>
          ⚡ <strong>الأولوية:</strong> {ticket.priority || "—"}
        </div>

        {/* ✅ الموقع: إما Studio أو نص حر */}
        <div style={{ marginBottom: 8 }}>
          🏢 <strong>الموقع:</strong> {ticket.location}
        </div>

        <div style={{ marginBottom: 8 }}>
          📱 <strong>رقم المبلّغ:</strong> {ticket.contact}
        </div>

        <div style={{ marginTop: 12 }}>
          💬 <strong>ملاحظات عامة:</strong>{" "}
          {ticket.public_notes || "لا توجد ملاحظات"}
        </div>
      </div>
    </div>
  );
}

export default Tracker;
