import React, { useEffect, useState } from "react";

const API_BASE = "http://localhost:3001/api";
const ADMIN_PASS = "admin123"; // change later

function OpsDashboard() {
  const [auth, setAuth] = useState(false);
  const [pass, setPass] = useState("");
  const [tickets, setTickets] = useState([]);
  const [kpi, setKpi] = useState({});
  const [error, setError] = useState(null);

  const login = () => {
    if (pass === ADMIN_PASS) setAuth(true);
    else setError("‚ùå ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©");
  };

  const fetchData = () => {
    fetch(`${API_BASE}/tickets-all`)
      .then((r) => r.json())
      .then((data) => setTickets(data.tickets || []))
      .catch(() => setError("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ∞ÿßŸÉÿ±"));

    fetch(`${API_BASE}/tickets-kpi`)
      .then((r) => r.json())
      .then(setKpi)
      .catch(() => setError("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ŸÖÿ§ÿ¥ÿ±ÿßÿ™ ÿßŸÑÿ£ÿØÿßÿ°"));
  };

  useEffect(() => {
    if (auth) fetchData();
  }, [auth]);

  const updateTicket = async (id) => {
    try {
      const res = await fetch(`${API_BASE}/tickets-update`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id,
          status: document.getElementById(`status-${id}`).value,
          public_notes: document.getElementById(`note-${id}`).value,
          tech_name: document.getElementById(`techname-${id}`).value,
          tech_phone: document.getElementById(`techphone-${id}`).value,
        }),
      });
      const data = await res.json();
      if (data.success) fetchData();
      else alert("‚ùå ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´");
    } catch {
      alert("‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ");
    }
  };

  if (!auth) {
    return (
      <div style={{ padding: 16, textAlign: "center" }}>
        <h2>üîê Ops Dashboard</h2>
        <input
          type="password"
          placeholder="Password"
          onChange={(e) => setPass(e.target.value)}
        />
        <button onClick={login}>Login</button>
        {error && <p style={{ color: "red" }}>{error}</p>}
      </div>
    );
  }

  return (
    <div style={{ padding: 16, direction: "rtl" }}>
      <h2 style={{ color: "#003366" }}>ŸÑŸàÿ≠ÿ© ÿßŸÑÿµŸäÿßŸÜÿ©</h2>

      {/* KPI Cards */}
      <div style={{ display: "flex", gap: 16, flexWrap: "wrap", marginBottom: 20 }}>
        {Object.entries(kpi).map(([key, val]) => (
          <div
            key={key}
            style={{
              flex: "1 1 120px",
              padding: 16,
              background: "#fff",
              borderRadius: "10px",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              textAlign: "center",
            }}
          >
            <h3 style={{ margin: 0, color: "#006633" }}>{val}</h3>
            <p>{key}</p>
          </div>
        ))}
      </div>

      <a
        href={`${API_BASE}/tickets-export`}
        target="_blank"
        rel="noreferrer"
        style={{
          display: "inline-block",
          padding: "10px 16px",
          background: "#006633",
          color: "#fff",
          borderRadius: "8px",
          marginBottom: 16,
          textDecoration: "none",
        }}
      >
        ‚¨áÔ∏è ÿ™ÿ≠ŸÖŸäŸÑ CSV
      </a>

      {/* Tickets Table */}
      <table
        border="1"
        cellPadding="6"
        style={{ borderCollapse: "collapse", width: "100%", background: "#fff" }}
      >
        <thead style={{ background: "#003366", color: "#fff" }}>
          <tr>
            <th>ÿßŸÑÿ±ŸÇŸÖ</th>
            <th>ÿßŸÑÿπŸÜŸàÿßŸÜ</th>
            <th>ÿßŸÑŸàÿµŸÅ</th>
            <th>ÿßŸÑŸÅÿ¶ÿ©</th>
            <th>ÿßŸÑÿ£ŸàŸÑŸàŸäÿ©</th>
            <th>ÿßŸÑŸÖŸàŸÇÿπ</th>
            <th>ÿßŸÑÿ¨ŸàÿßŸÑ</th>
            <th>ÿßŸÑÿ≠ÿßŸÑÿ©</th>
            <th>ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™</th>
            <th>ÿßŸÑŸÅŸÜŸä</th>
            <th>ÿ¨ŸàÿßŸÑ ÿßŸÑŸÅŸÜŸä</th>
            <th>ÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
          </tr>
        </thead>
        <tbody>
          {tickets.map((t) => (
            <tr key={t.id}>
              <td>{t.id}</td>
              <td>{t.title}</td>
              <td>{t.description}</td>
              <td>{t.category}</td>
              <td>{t.priority}</td>
              <td>{t.location}</td>
              <td>{t.contact}</td>
              <td>
                <select defaultValue={t.status} id={`status-${t.id}`}>
                  <option>Open</option>
                  <option>In Progress</option>
                  <option>Completed</option>
                  <option>On Hold</option>
                  <option>Referred</option>
                  <option>Cannot Access</option>
                </select>
              </td>
              <td>
                <input
                  type="text"
                  defaultValue={t.public_notes}
                  id={`note-${t.id}`}
                  style={{ width: "120px" }}
                />
              </td>
              <td>
                <input
                  type="text"
                  defaultValue={t.tech_name}
                  id={`techname-${t.id}`}
                  style={{ width: "100px" }}
                />
              </td>
              <td>
                <input
                  type="text"
                  defaultValue={t.tech_phone}
                  id={`techphone-${t.id}`}
                  style={{ width: "100px" }}
                />
              </td>
              <td>
                <button onClick={() => updateTicket(t.id)}>üíæ ÿ≠ŸÅÿ∏</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default OpsDashboard;
