import React, { useState } from "react";

const translations = {
  ar: {
    title: "إرسال بلاغ جديد",
    placeholder_title: "عنوان البلاغ",
    placeholder_desc: "وصف البلاغ",
    placeholder_contact: "رقم الجوال",
    category: "الفئة",
    priority: "الأولوية",
    location: "الموقع",
    photo: "صورة (اختياري)",
    submit: "إرسال",
    myReports: "متابعة بلاغاتي",
    langSwitch: "English",
  },
  en: {
    title: "Submit New Request",
    placeholder_title: "Request Title",
    placeholder_desc: "Description",
    placeholder_contact: "Phone Number",
    category: "Category",
    priority: "Priority",
    location: "Location",
    photo: "Photo (optional)",
    submit: "Submit",
    myReports: "Track My Requests",
    langSwitch: "العربية",
  },
};

function Requester() {
  const [lang, setLang] = useState("ar");

  return (
    <div style={{ padding: 16, direction: lang === "ar" ? "rtl" : "ltr" }}>
      <button onClick={() => setLang(lang === "ar" ? "en" : "ar")}>
        {translations[lang].langSwitch}
      </button>

      <h2>{translations[lang].title}</h2>

      <input placeholder={translations[lang].placeholder_title} />
      <textarea placeholder={translations[lang].placeholder_desc} />
      <input placeholder={translations[lang].placeholder_contact} />

      <div>{translations[lang].category}</div>
      <div>{translations[lang].priority}</div>
      <div>{translations[lang].location}</div>
      <div>{translations[lang].photo}</div>

      <button>{translations[lang].submit}</button>

      <h3>{translations[lang].myReports}</h3>
    </div>
  );
}

export default Requester;
