import React, { useState } from "react";

const API_BASE = "http://localhost:3001/api";

function Requester() {
  const [form, setForm] = useState({
    title: "",
    description: "",
    category: "",
    priority: "",
    location: "",
    contact: ""
  });
  const [ticketId, setTicketId] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const submitTicket = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const res = await fetch(`${API_BASE}/tickets`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (data.ticketId) {
        setTicketId(data.ticketId);
      } else {
        setError("❌ لم يتم إنشاء البلاغ، تحقق من البيانات");
      }
    } catch (err) {
      setError("⚠️ خطأ في الاتصال بالخادم");
    }
  };

  const trackByPhone = async () => {
    if (!form.contact) {
      setError("⚠️ أدخل رقم الجوال أولاً");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${API_BASE}/tickets?contact=${form.contact}`);
      const data = await res.json();
      if (data.tickets && data.tickets.length > 0) {
        setTickets(data.tickets);
      } else {
        setTickets([]);
        setError("❌ لا توجد بلاغات مرتبطة بهذا الرقم");
      }
    } catch (err) {
      setError("⚠️ خطأ في الاتصال بالخادم");
    }
    setLoading(false);
  };

  const inputStyle = {
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "14px",
    width: "100%",
  };

  const buttonStyle = {
    background: "#006633",
    color: "#fff",
    padding: "12px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "16px",
  };

  return (
    <div style={{ padding: 16, maxWidth: 500, margin: "auto", direction: "rtl" }}>
      <h2 style={{ color: "#003366", textAlign: "center", marginBottom: 20 }}>
        📋 إرسال بلاغ جديد
      </h2>

      <form
        onSubmit={submitTicket}
        style={{
          display: "flex",
          flexDirection: "column",
          gap: 12,
          background: "#fff",
          padding: 20,
          borderRadius: "12px",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <input name="title" placeholder="عنوان البلاغ" onChange={handleChange} style={inputStyle} required />
        <textarea
          name="description"
          placeholder="الوصف"
          onChange={handleChange}
          style={{ ...inputStyle, minHeight: "80px" }}
          required
        />
        <select name="category" onChange={handleChange} style={inputStyle} required>
          <option value="">اختر الفئة</option>
          <option>Electrical</option>
          <option>Mechanical</option>
          <option>Cleaning</option>
        </select>
        <select name="priority" onChange={handleChange} style={inputStyle} required>
          <option value="">الأولوية</option>
          <option>High</option>
          <option>Medium</option>
          <option>Low</option>
        </select>
        <select name="location" onChange={handleChange} style={inputStyle} required>
          <option value="">اختر الاستوديو</option>
          {[...Array(14)].map((_, i) => (
            <option key={i}>Studio {i + 1}</option>
          ))}
        </select>
        <input name="contact" placeholder="رقم الجوال" onChange={handleChange} style={inputStyle} required />
        <button type="submit" style={buttonStyle}>📨 إرسال البلاغ</button>
      </form>

      {ticketId && (
        <div
          style={{
            marginTop: 20,
            padding: 16,
            background: "#e6ffed",
            border: "1px solid #00a152",
            borderRadius: "8px",
            color: "#006633",
          }}
        >
          ✅ تم إنشاء التذكرة بنجاح <br /> رقمها: <b>{ticketId}</b>
        </div>
      )}

      {error && (
        <div
          style={{
            marginTop: 16,
            padding: 12,
            background: "#ffe6e6",
            border: "1px solid #ff4d4d",
            borderRadius: "8px",
            color: "red",
          }}
        >
          {error}
        </div>
      )}

      <hr style={{ margin: "24px 0" }} />

      <h3 style={{ color: "#003366" }}>🔎 متابعة بلاغاتي</h3>
      <button onClick={trackByPhone} style={buttonStyle}>
        📂 عرض البلاغات
      </button>

      {loading && <p>⏳ جاري تحميل البلاغات...</p>}

      <ul style={{ listStyle: "none", padding: 0, marginTop: 12 }}>
        {tickets.map((t) => (
          <li
            key={t.id}
            style={{
              background: "#f0f8ff",
              marginBottom: 8,
              padding: 10,
              borderRadius: "8px",
              border: "1px solid #ccc",
            }}
          >
            <b>{t.id}</b> - {t.status} - {t.location}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Requester;
